ettara_sentiment_value = {'positive': 0, 'negative': 0, 'neutral': 0}
tea_villa_sentiment_value = {'positive': 0, 'negative': 0, 'neutral': 0}
love_latte_sentiment_value = {'positive': 0, 'negative': 0, 'neutral': 0}


def analyze_sentiment(review_text):
    sia = SentimentIntensityAnalyzer()
    sentiment_score = sia.polarity_scores(review_text)
    return sentiment_score


def extract_keywords(review_text):
    tokens = word_tokenize(review_text.lower())
    
    # Filter out stopwords and punctuation
    stop_words = set(stopwords.words('english'))
    tokens = [word for word in tokens if word not in stop_words and word not in string.punctuation]
    
    keywords = Counter(tokens).most_common(4)
    return [keyword[0] for keyword in keywords]



def get_review_analysis():
    for review in ettara_reviews:
        sentiment_score = analyze_sentiment(review)
        if sentiment_score['compound'] >= 0.05:
            sentiment = 'Positive'
            ettara_sentiment_value['positive'] += 1
        elif sentiment_score['compound'] <= -0.05:
            sentiment = 'Negative'
            ettara_sentiment_value['negative'] += 1
        else:
            sentiment = 'Neutral'
            ettara_sentiment_value['neutral'] += 1
    
    for review in tea_villa_reviews:
        sentiment_score = analyze_sentiment(review)
        if sentiment_score['compound'] >= 0.05:
            sentiment = 'Positive'
            tea_villa_sentiment_value['positive'] += 1
        elif sentiment_score['compound'] <= -0.05:
            sentiment = 'Negative'
            tea_villa_sentiment_value['negative'] += 1
        else:
            sentiment = 'Neutral'
            tea_villa_sentiment_value['neutral'] += 1

    for review in love_latte_reviews:
        sentiment_score = analyze_sentiment(review)
        if sentiment_score['compound'] >= 0.05:
            sentiment = 'Positive'
            love_latte_sentiment_value['positive'] += 1
        elif sentiment_score['compound'] <= -0.05:
            sentiment = 'Negative'
            love_latte_sentiment_value['negative'] += 1
        else:
            sentiment = 'Neutral'
            love_latte_sentiment_value['neutral'] += 1

    return {
        'ettara' : ettara_sentiment_value,
        'love_latte': love_latte_sentiment_value,
        'tea_villa' : tea_villa_sentiment_value
    }